async () => {
  const users = await getUsers.run();
  return users.map(u => {
    return {
      FirstName: u.first_name,
      LastName: u.last_name,
      AccountType: u.role,
      Email: u.email,
      DateAdded: u.created,
      LastActive: u.last_login,
      Id: u.id
    };
  });
}

