actionConfiguration.body:
status => {
  if (status === 'Approved' || status === 'Disbursed' || status === 'Active') {
    return 'RGB(0, 128, 0)';
  }
  if (status === 'Rejected' || status === 'Late' || status === 'At Risk' || status === 'Closed') {
    return 'RGB(255, 0, 0)';
  }
  return 'RGB(255, 165, 0)';
}

